import { Component, OnInit } from '@angular/core';
import { CollegeService } from '../../services/college.service';

@Component({
  selector: 'app-college-list',
  templateUrl: './college-list.component.html',
  styleUrls: ['./college-list.component.scss'],
})
export class CollegeListComponent implements OnInit {
  colleges: any[] = [];
  currentPage = 0;
  totalResults = 0;
  perPage = 20;
  searchTerm: string = '';

  constructor(private collegeService: CollegeService) {}

  ngOnInit(): void {
    this.loadColleges();
  }

  loadColleges(): void {
    this.collegeService.getColleges(this.currentPage, this.perPage).subscribe(
      (data) => {
        this.colleges = [...this.colleges, ...data.colleges];
        this.totalResults = data.totalResults;
        this.currentPage = data.currentPage;
      },
      (error) => console.error('Error fetching colleges:', error)
    );
  }

  loadMore(): void {
    this.currentPage++;
    this.loadColleges();
  }

  searchColleges(): void {
    if (this.searchTerm) {
      this.collegeService.searchColleges(this.searchTerm).subscribe(
        (colleges) => {
          this.colleges = colleges;
          this.totalResults = colleges.length;
          this.currentPage = 0;
        },
        (error) => console.error('Error searching colleges:', error)
      );
    } else {
      this.colleges = [];
      this.currentPage = 0;
      this.loadColleges();
    }
  }
}
